import { useState } from 'react';
import { Formik, Form, useField } from 'formik';
import * as Yup from 'yup';

const url = new URL(window.location.href);

const TextInput = ({ label, helpText, ...props }) => {
    const [field, meta] = useField(props);
    const { name, value } = field;
    const [didFocus, setDidFocus] = useState(false);

    const handleFocus = () => setDidFocus(true);
    console.log(`field: `, field);
    console.log(`meta.touched: `, meta.touched);
    const showFeedback = (!!didFocus && field.value.trim().length > 2) || meta.touched;

    url.searchParams.set(name, value);
    // url.searchParams.set(name, JSON.parse(window.sessionStorage.getItem(name)));
    window.sessionStorage.setItem(name, JSON.stringify(value));
    return (
        <div className={`form-control ${showFeedback ? (meta.error ? 'invalid' : 'valid') : ''}`}>
            <div className="flex items-center space-between">
                <label htmlFor={props.id || props.name}>{label}</label>
                {showFeedback ? (
                    <div
                        id={`${props.id}-feedback`}
                        aria-live="polite"
                        className="feedback text-sm"
                    >
                        {meta.error ? meta.error : '✓'}
                    </div>
                ) : null}
            </div>
            <input className="text-input" {...field} {...props} />
            {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
        </div>
    );
};

const Checkbox = ({ children, ...props }) => {
    const [field, meta] = useField({ ...props, type: 'checkbox' });
    // const data = useField({ ...props, type: 'checkbox' });
    return (
        <div>
            <label className="checkbox-input">
                <input type="checkbox" {...field} {...props} />
                {children}
            </label>
            {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
        </div>
    );
};

const Select = ({ label, ...props }) => {
    const [field, meta] = useField(props);
    return (
        <div>
            <label htmlFor={props.id || props.name}>{label}</label>
            <select {...field} {...props} />
            {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
        </div>
    );
};

const FormComponent = () => {
    return (
        <>
            <h1>Welcome</h1>
            <h2>Let's create your account!</h2>
            <Formik
                initialValues={{
                    firstName:
                        url.searchParams.get('firstName') ||
                        JSON.parse(window.sessionStorage.getItem('firstName')) ||
                        '',
                    lastName:
                        url.searchParams.get('lastName') ||
                        JSON.parse(window.sessionStorage.getItem('lastName')) ||
                        '',
                    email:
                        url.searchParams.get('email') ||
                        JSON.parse(window.sessionStorage.getItem('email')) ||
                        '',
                    acceptedTerms: false,
                    jobType: '',
                }}
                validationSchema={Yup.object({
                    firstName: Yup.string()
                        .max(15, 'Must be 15 characters or less')
                        .required('Required'),
                    lastName: Yup.string()
                        .max(20, 'Must be 20 characters or less')
                        .required('Required'),
                    email: Yup.string().email('Invalid email address').required('Required'),
                    acceptedTerms: Yup.boolean()
                        .required('Required')
                        .oneOf([true], 'You must accept the terms and conditions.'),
                    jobType: Yup.string()
                        .oneOf(['designer', 'development', 'product', 'other'], 'Invalid Job Type')
                        .required('Required'),
                })}
                onSubmit={(values, data) => {
                    setTimeout(() => {
                        data.setSubmitting(true);
                        alert(JSON.stringify(values, null, 2));
                        data.setSubmitting(false);
                        window.sessionStorage.clear();
                        data.resetForm();
                    }, 400);
                }}
            >
                {data => (
                    <Form>
                        <TextInput
                            label="First Name"
                            name="firstName"
                            type="text"
                            placeholder="Name"
                        />

                        <TextInput
                            label="Last Name"
                            name="lastName"
                            type="text"
                            placeholder="Surname"
                        />

                        <TextInput
                            label="Email Address"
                            name="email"
                            type="text"
                            placeholder="Email"
                        />

                        <Select label="Job Type" name="jobType">
                            <option value="">Select a job type</option>
                            <option value="designer">Designer</option>
                            <option value="development">Developer</option>
                            <option value="product">Product Manager</option>
                            <option value="other">Other</option>
                        </Select>
                        <Checkbox name="acceptedTerms">I accept the terms and conditions</Checkbox>

                        <button
                            type="submit"
                            disabled={data.isSubmitting}
                            // onClick={() => console.log(`window`, url.searchParams.get('firstName'))}
                        >
                            {data.isSubmitting ? 'Processing...' : 'Submit'}
                        </button>
                    </Form>
                )}
            </Formik>
        </>
    );
};

export default FormComponent;
